        this.positivod = false;
        this.negativod = false;
        this.resultado1sesioninicial = 'Se presenta en qué consiste el trabajo final de graduación y se firman los consentimientos informados de las personas interesadas. Se continúa con el trabajo de campo, específicamente con la recopilación de las historias.';
        this.resultado2sesioninicial = 'Se termina el trabajo de campo y se deja como una propuesta teórica.';
        this.resultado1historias = 'Se puede continuar con el trabajo de campo, se inicia la aplicación de la propuesta.';
        this.resultado2historias = 'Se termina el trabajo de campo, se deja como una propuesta teórica. Luego se procede a la evaluación de la sesión inicial y recopilación del material audiovisual.';
        this.resultado1evaluacion = 'Se hace la evaluación de las sesiones y se procede a la retroalimentación.';
